contracts/MedicineInventory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/access/AccessControl.sol";

///  Medicine Inventory & Staff Receipt Tracking
///  Manages medicine stock and records staff-issued receipts securely with role-based access control.
contract MedicineInventory is AccessControl {
    // Define staff role for permissioned receipt entry
    bytes32 public constant STAFF_ROLE = keccak256("STAFF_ROLE");

    // Contract constructor sets an admin role upon deployment
    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
    }

    /// Stores information about medicine inventory
    struct Medicine {
        string name;
        string batchNumber; // Unique batch identifier
        string notes;
        uint256 quantity;   // Available units
        uint256 expirationDate; // Unix timestamp of expiry
        uint256 addedDate;  
        uint256 lastUpdated; // Tracks last update time
        string location; // Storage location (optional)
    }

    /// Stores information about staff-issued medicine receipts
    struct StaffReceipt {
        string patientName;
        string staffName;
        string medicineName;
        uint256 quantity;
        string notes;
        uint256 timestamp; // Timestamp when receipt was issued
    }

    // Arrays to hold medicine inventory and receipt records
    Medicine[] public medicines;
    StaffReceipt[] public receipts;

    // Staff member tracking arrays and mappings
    address[] public staffMembers;
    mapping(address => bool) public isStaffMember;

    // Mapping to prevent duplicate batch numbers
    mapping(string => mapping(string => bool)) public batchExists;



    // ENHANCED EVENTS FOR BETTER TRANSACTION HISTORY
    event MedicineAdded(string name, uint256 quantity, uint256 expirationDate);
    event MedicineUpdated(uint256 index, uint256 newQuantity);
    event MedicineRemoved(uint256 index, string name);
    event ReceiptAdded(string patientName, string staffName, string medicineName, uint256 quantity, string notes);

    // Staff management events
    event StaffRoleGranted(address indexed staff, address indexed admin, uint256 timestamp);
    event StaffRoleRevoked(address indexed staff, address indexed admin, uint256 timestamp);

    // Enhanced history tracking events
    event MedicineHistoryLog(
        uint256 indexed medicineId,
        string action, // "CREATED", "UPDATED", "DELETED"
        string fieldChanged, // "name", "quantity", "batchNumber", "notes", "expirationDate", "location", "ALL"
        string oldValue,
        string newValue,
        address changedBy,
        uint256 timestamp,
        string description // Human-readable description
    );

    event ReceiptHistoryLog(
        uint256 indexed receiptId,
        string action, // "CREATED", "UPDATED", "DELETED"
        string fieldChanged, // "patientName", "staffName", "medicineName", "quantity", "notes", "ALL"
        string oldValue,
        string newValue,
        address changedBy,
        uint256 timestamp,
        string description // Human-readable description
    );

    // Keep your existing events for backward compatibility
    event MedicineUpdatedFull(
        uint256 indexed index,
        bytes oldData,
        bytes newData,
        uint256 timestamp
    );
    event MedicineAddedFull(
        uint256 indexed index,
        string name,
        string batchNumber,
        string notes,
        uint256 quantity,
        uint256 expirationDate,
        string location,
        uint256 timestamp
    );
    event ReceiptUpdated(uint256 index, string patientName, string staffName, string medicineName, uint256 quantity, string notes, uint256 timestamp);
    event ReceiptRemoved(uint256 index);

    // Custom modifier to allow both admins and staff to access receipt functions
    modifier onlyStaffOrAdmin() {
        require(
            hasRole(STAFF_ROLE, msg.sender) || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "AccessControl: caller must have staff or admin role"
        );
        _;
    }

    ///  Adds a new medicine entry (Restricted to Admin)
    function addMedicine(
        string memory _name,
        string memory _batchNumber,
        string memory _notes,
        uint256 _quantity,
        uint256 _expirationDate,
        string memory _location
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        // ✅ Only require uniqueness per medicine name + batch number
        require(!batchExists[_name][_batchNumber], "Batch number already exists for this medicine");

        medicines.push(Medicine({
            name: _name,
            batchNumber: _batchNumber,
            notes: _notes,
            quantity: _quantity,
            expirationDate: _expirationDate,
            addedDate: block.timestamp,
            lastUpdated: block.timestamp,
            location: _location
        }));

        // ✅ Store the batch number under this medicine name
        batchExists[_name][_batchNumber] = true;
        uint256 newIndex = medicines.length - 1;

        emit MedicineAdded(_name, _quantity, _expirationDate);
        emit MedicineAddedFull(
            newIndex,
            _name,
            _batchNumber,
            _notes,
            _quantity,
            _expirationDate,
            _location,
            block.timestamp
        );

        // Enhanced history log
        emit MedicineHistoryLog(
            newIndex,
            "CREATED",
            "ALL",
            "",
            string(abi.encodePacked(_name, " (", _batchNumber, ")")),
            msg.sender,
            block.timestamp,
            string(abi.encodePacked("Added new medicine: ", _name, " with batch ", _batchNumber))
        );
    }

    ///  Allows authorized staff OR admin to add a receipt
    function addStaffReceipt(
        string memory _patientName,
        string memory _staffName,
        string memory _medicineName,
        uint256 _quantity,
        string memory _notes
    ) public onlyStaffOrAdmin {
        receipts.push(StaffReceipt({
            patientName: _patientName,
            staffName: _staffName,
            medicineName: _medicineName,
            quantity: _quantity,
            notes: _notes,
            timestamp: block.timestamp
        }));

        uint256 newIndex = receipts.length - 1;

        emit ReceiptAdded(_patientName, _staffName, _medicineName, _quantity, _notes);

        // Enhanced history log with role identification
        string memory roleDescription = hasRole(DEFAULT_ADMIN_ROLE, msg.sender) ? "Admin" : "Staff";
        emit ReceiptHistoryLog(
            newIndex,
            "CREATED",
            "ALL",
            "",
            string(abi.encodePacked(_medicineName, " for ", _patientName)),
            msg.sender,
            block.timestamp,
            string(abi.encodePacked(roleDescription, " created receipt for ", _patientName, " - ", _medicineName, " x", toString(_quantity)))
        );
    }

    /// Grants STAFF_ROLE to a new staff member (Only Admin) - Updated with tracking
    function grantStaffRole(address staff) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(STAFF_ROLE, staff);

        // Add to staff members array if not already present
        if (!isStaffMember[staff]) {
            staffMembers.push(staff);
            isStaffMember[staff] = true;
        }

        emit StaffRoleGranted(staff, msg.sender, block.timestamp);
    }

    /// Revokes STAFF_ROLE from a staff member (Only Admin)
    function revokeStaffRole(address staff) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(STAFF_ROLE, staff);

        // Remove from staff members array
        if (isStaffMember[staff]) {
            for (uint i = 0; i < staffMembers.length; i++) {
                if (staffMembers[i] == staff) {
                    // Move last element to current position and pop
                    staffMembers[i] = staffMembers[staffMembers.length - 1];
                    staffMembers.pop();
                    break;
                }
            }
            isStaffMember[staff] = false;
        }

        emit StaffRoleRevoked(staff, msg.sender, block.timestamp);
    }

    /// Returns all staff member addresses
    function getStaffMembers() public view returns (address[] memory) {
        return staffMembers;
    }

    /// Returns the total number of staff members
    function getStaffCount() public view returns (uint256) {
        return staffMembers.length;
    }

    ///  Fetches a receipt entry by index
    function getReceipt(uint256 _index) public view returns (StaffReceipt memory) {
        return receipts[_index];
    }

    /// Returns total receipt count
    function getReceiptCount() public view returns (uint256) {
        return receipts.length;
    }

    // Enhanced function to update medicine stock with detailed history
    function updateMedicine(
        uint256 _index,
        string memory _name,
        string memory _batchNumber,
        string memory _notes,
        uint256 _quantity,
        uint256 _expirationDate,
        string memory _location
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_index < medicines.length, "Medicine index out of bounds");

        Medicine storage med = medicines[_index];

        // Capture old values
        string memory oldName = med.name;
        string memory oldBatchNumber = med.batchNumber;
        string memory oldNotes = med.notes;
        uint256 oldQuantity = med.quantity;
        uint256 oldExpirationDate = med.expirationDate;
        string memory oldLocation = med.location;

        // Track individual field changes
        if (keccak256(abi.encodePacked(oldName)) != keccak256(abi.encodePacked(_name))) {
            emit MedicineHistoryLog(
                _index,
                "UPDATED",
                "name",
                oldName,
                _name,
                msg.sender,
                block.timestamp,
                string(abi.encodePacked("Changed medicine name from '", oldName, "' to '", _name, "'"))
            );
        }

        if (keccak256(abi.encodePacked(oldBatchNumber)) != keccak256(abi.encodePacked(_batchNumber))) {
            require(!batchExists[_name][_batchNumber], "Batch number already exists for this medicine");
            batchExists[oldName][oldBatchNumber] = false;
            batchExists[_name][_batchNumber] = true;

            emit MedicineHistoryLog(
                _index,
                "UPDATED",
                "batchNumber",
                oldBatchNumber,
                _batchNumber,
                msg.sender,
                block.timestamp,
                string(abi.encodePacked("Changed batch number from '", oldBatchNumber, "' to '", _batchNumber, "'"))
            );
        }

        if (oldQuantity != _quantity) {
            emit MedicineHistoryLog(
                _index,
                "UPDATED",
                "quantity",
                toString(oldQuantity),
                toString(_quantity),
                msg.sender,
                block.timestamp,
                string(abi.encodePacked("Changed quantity from ", toString(oldQuantity), " to ", toString(_quantity)))
            );
        }

        if (keccak256(abi.encodePacked(oldLocation)) != keccak256(abi.encodePacked(_location))) {
            emit MedicineHistoryLog(
                _index,
                "UPDATED",
                "location",
                oldLocation,
                _location,
                msg.sender,
                block.timestamp,
                string(abi.encodePacked("Changed location from '", oldLocation, "' to '", _location, "'"))
            );
        }

        // Update medicine
        med.name = _name;
        med.batchNumber = _batchNumber;
        med.notes = _notes;
        med.quantity = _quantity;
        med.expirationDate = _expirationDate;
        med.lastUpdated = block.timestamp;
        med.location = _location;

        // Emit existing event for backward compatibility
        emit MedicineUpdatedFull(
            _index,
            abi.encode(oldName, oldBatchNumber, oldNotes, oldQuantity, oldExpirationDate, oldLocation),
            abi.encode(_name, _batchNumber, _notes, _quantity, _expirationDate, _location),
            block.timestamp
        );
    }

    // Enhanced staff receipt update with history tracking - Now accessible by both staff and admin
    function updateStaffReceipt(
        uint256 _index,
        string memory _patientName,
        string memory _staffName,
        string memory _medicineName,
        uint256 _quantity,
        string memory _notes
    ) public onlyStaffOrAdmin {
        require(_index < receipts.length, "Receipt index out of bounds");

        StaffReceipt storage receipt = receipts[_index];

        // Capture old values
        string memory oldPatientName = receipt.patientName;
        string memory oldStaffName = receipt.staffName;
        string memory oldMedicineName = receipt.medicineName;
        uint256 oldQuantity = receipt.quantity;
        string memory oldNotes = receipt.notes;

        // Track individual field changes with role identification
        string memory roleDescription = hasRole(DEFAULT_ADMIN_ROLE, msg.sender) ? "Admin" : "Staff";

        if (keccak256(abi.encodePacked(oldPatientName)) != keccak256(abi.encodePacked(_patientName))) {
            emit ReceiptHistoryLog(
                _index,
                "UPDATED",
                "patientName",
                oldPatientName,
                _patientName,
                msg.sender,
                block.timestamp,
                string(abi.encodePacked(roleDescription, " changed patient name from '", oldPatientName, "' to '", _patientName, "'"))
            );
        }

        if (keccak256(abi.encodePacked(oldMedicineName)) != keccak256(abi.encodePacked(_medicineName))) {
            emit ReceiptHistoryLog(
                _index,
                "UPDATED",
                "medicineName",
                oldMedicineName,
                _medicineName,
                msg.sender,
                block.timestamp,
                string(abi.encodePacked(roleDescription, " changed medicine from '", oldMedicineName, "' to '", _medicineName, "'"))
            );
        }

        if (oldQuantity != _quantity) {
            emit ReceiptHistoryLog(
                _index,
                "UPDATED",
                "quantity",
                toString(oldQuantity),
                toString(_quantity),
                msg.sender,
                block.timestamp,
                string(abi.encodePacked(roleDescription, " changed quantity from ", toString(oldQuantity), " to ", toString(_quantity)))
            );
        }

        receipt.patientName = _patientName;
        receipt.staffName = _staffName;
        receipt.medicineName = _medicineName;
        receipt.quantity = _quantity;
        receipt.notes = _notes;
        receipt.timestamp = block.timestamp; // Update timestamp

        emit ReceiptUpdated(_index, _patientName, _staffName, _medicineName, _quantity, _notes, block.timestamp);
    }

    // Get medicine by index
    function getMedicine(uint256 _index)
        public
        view
        returns (
            string memory name,
            string memory batchNumber,
            string memory notes,
            uint256 quantity,
            uint256 expirationDate,
            uint256 addedDate,  
            uint256 lastUpdated, 
            string memory location,
            bool isExpired
        )
    {
        Medicine memory med = medicines[_index];
        bool expired = block.timestamp >= med.expirationDate;
        return (
            med.name,
            med.batchNumber,
            med.notes,
            med.quantity,
            med.expirationDate,
            med.addedDate,
            med.lastUpdated,
            med.location,
            expired
        );
    }

    // Check if medicine is expired
    function isMedicineExpired(uint256 _index) public view returns (bool) {
        require(_index < medicines.length, "Medicine index out of bounds");
        return block.timestamp >= medicines[_index].expirationDate;
    }

    // Remove medicine entry with history tracking
    function removeMedicine(uint256 _index) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_index < medicines.length, "Medicine index out of bounds");

        string memory removedName = medicines[_index].name;
        string memory removedBatch = medicines[_index].batchNumber;

        emit MedicineHistoryLog(
            _index,
            "DELETED",
            "ALL",
            string(abi.encodePacked(removedName, " (", removedBatch, ")")),
            "",
            msg.sender,
            block.timestamp,
            string(abi.encodePacked("Deleted medicine: ", removedName, " with batch ", removedBatch))
        );

        if (_index < medicines.length - 1) {
            medicines[_index] = medicines[medicines.length - 1];
        }
        medicines.pop();

        emit MedicineRemoved(_index, removedName);
    }

    // Remove staff receipt with history tracking - Now accessible by both staff and admin
    function removeStaffReceipt(uint256 _index) public onlyStaffOrAdmin {
        require(_index < receipts.length, "Receipt index out of bounds");

        StaffReceipt memory removedReceipt = receipts[_index];
        string memory roleDescription = hasRole(DEFAULT_ADMIN_ROLE, msg.sender) ? "Admin" : "Staff";

        emit ReceiptHistoryLog(
            _index,
            "DELETED",
            "ALL",
            string(abi.encodePacked(removedReceipt.medicineName, " for ", removedReceipt.patientName)),
            "",
            msg.sender,
            block.timestamp,
            string(abi.encodePacked(roleDescription, " deleted receipt for ", removedReceipt.patientName, " - ", removedReceipt.medicineName))
        );

        if (_index < receipts.length - 1) {
            receipts[_index] = receipts[receipts.length - 1]; // Move last to deleted slot
        }
        receipts.pop(); // Remove last

        emit ReceiptRemoved(_index);
    }

    // Get total number of medicines tracked
    function getMedicineCount() public view returns (uint256) {
        return medicines.length;
    }

    // Utility function to convert uint256 to string
    function toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

